import React, {useState, useEffect} from 'react';
import UploadBar from './UploadBar'
import ImageThumbnail from './ImageThumbnail'
import './css/Main.css'
import axios from 'axios'

function Main(props) {

    const [imageThumbnails, setImageThumbnails] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    //Retrieve files from the server
    const fetchData = () => {
        console.log(localStorage.getItem("authToken"));
        axios.get('http://localhost:5000/file/getImages',  {headers: 
            {
            "x-access-token": localStorage.getItem("authToken")
            }
        })
        .then((res) => {
            let newThumbnails = [];
            res.data.map((el, i) => {
                newThumbnails.push(<ImageThumbnail id={el.id}/>);
            });
            setImageThumbnails([...imageThumbnails, ...newThumbnails]);
        });
    }

    //Callback to display new files when they are uploaded by the user.
    const addedNewFile = (id) => {
        setImageThumbnails([...imageThumbnails, <ImageThumbnail id={id}/>]);
    }

    return(
        <div>
            <div className="row gy-4 main-grid mx-auto pt-3">
                {imageThumbnails}
            </div> 
            <UploadBar newFileCallback={addedNewFile}/>
        </div>
    )
} 

export default Main;
