import React, {useState, useEffect} from 'react';
import PostThumbnail from './PostThumbnail'
import './css/Main.css'
import axios from 'axios'
import NewPostThumbnail from './NewPostThumbnail';

function Main(props) {
    const [imageIds, setImageIds] = useState([]);

    useEffect(() => {
        if (props.currentServerId != null) {
            axios.get(`http://localhost:5000/servers/getPosts/${props.currentServerId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                let newIds = [];
                res.data.map((el, i) => {
                    newIds.push(el.dataId);
                });
            
                setImageIds(newIds);

            });
        } 
    }, [props.currentServerId]);

    
     //Callback to display new files when they are uploaded by the user.
     //TODO why is imagethumbnails cleared out when addednewfile is called?
     const addedNewFile = (id) => {
        setImageIds([...imageIds, id]);
    }

    return(
        <div className="container">
            <div className="row gy-4 mx-auto pt-3">
                <NewPostThumbnail enterPostCreation={props.enterPostCreation}/>
                {imageIds.map((el, i) => (
                    <PostThumbnail id={el} key={el} viewPost={props.viewPost}/>
                ))}
            </div> 
            {/* <UploadBar newFileCallback={addedNewFile} currentServerId={props.currentServerId}/> */}
        </div>
    )
} 

export default Main;
