import React from 'react';
import './css/UploadBar.css';
import {Form} from 'react-bootstrap';
import axios from 'axios';

class UploadBar extends React.Component {
    render() {
        return(
            <div className=" p-1 mt-3">
                <Form.Group controlId="formFile" className="mb-3" action="/file" method="post" enctype="multipart/form-data">
                {/* <Form.Control type="file" onChange={(event) => {
                    axios.post("http://localhost:5000/file", {path: event.target.value})
                    .then((res) => console.log('succ'))
                    .catch((error) => {
                        console.log(error);
    
                    });
                }}/> */}
                <Form.Control type="file" name="avatar" onChange="this.submit()"/>
                </Form.Group>
            </div>
        )
    }
} 

export default UploadBar;
