import React, {useState, useEffect} from 'react';
import './css/Main.css';
import axios from 'axios';

function ImageThumbnail(props) {

    const [image, setImage] = useState(<div>{props.id}</div>);

    useEffect(() => {
        downloadImage();
    }, []);

    const downloadImage = () => {
        axios.get(`http://localhost:5000/file/${props.id}`)
        .then((res) => {
            var objectURL = URL.createObjectURL(res);
            setImage(<img src={objectURL}></img>)
        });
    }

    return(
        <div className="col">
            <div className="grid-item border border-secondary">
                <img src={`http://localhost:5000/file/${props.id}`}/>
            </div>
        </div>
    )
}

export default ImageThumbnail;
