import React, {useState, useEffect, useRef} from 'react';
import axios from 'axios';
import Avatar from '../thumbnails/Avatar';
import Loading from '../secondary/Loading'
import {useNavigate, useParams} from 'react-router-dom';

//This is going to be a more general profile section. Username/Avatar changing, managing list of servers. 
//We'll add a seperate account section for editing email address and whatever else comes up.

function ViewServer(props) {
    
    const [server, setServer] = useState(null);

    //The users permission with the server
    const [permission, setPermission] = useState();

    const [editServerName, setEditServerName] = useState();

    const [hoverOverAvatar, setHoverOverAvatar] = useState(false);
    const [previewAvatar, setPreviewAvatar] = useState();

    const navigate = useNavigate();
    const params = useParams();
    const formRef = useRef(null);

 

    useEffect(() => {
        console.log("VIEW SERVER");
        axios.get(`http://localhost:5000/servers/getServer/${params.serverId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
        }).then((res) => {
            setServer(res.data.serverObject);
            setPermission(res.data.permission);
            setEditServerName(res.data.serverObject.serverName);
        })
    }, []);

    const saveChanges = () => {
        const fd = new FormData();

        

        fd.append("editServername", editServerName);
        

        if (formRef.current.files[0] && formRef.current.files[0].name) {
            fd.append('file-upload', formRef.current.files[0], formRef.current.files[0].name);
        }

        if ((!formRef.current.files[0]) || (formRef.current.files[0].type.split('/')[0] === "image")) {
            axios.post(`http://localhost:5000/servers/updateServer/${server._id}`, fd, {
                headers: {
                    'x-access-token': localStorage.getItem("authToken"),
                    'Content-Type': 'multipart/form-data'
                }
            })
            .then((res) => {
                if (res.status === 200) {
                    //TODO: a Toast that says saved or something
                    setServer(res.data.serverObject);
                } 
            })
            .catch((err) => {
                console.log(err);
            });
        } else {
            //err
        };

       
    }

     //Exit view mode.
     const exitViewServer = () => {
        navigate(-1);
    }

    const mouseOverEvent = (e) => {
        if (permission.permissions >= 4) {
            setHoverOverAvatar(true);
        }
    }

    const mouseLeaveEvent = (e) => {
        if (permission.permissions >= 4) {
            setHoverOverAvatar(false);
        }
    }

    const onClickAvatar = (e) => {
        if (permission.permissions >= 4) {
            formRef.current.click();
        }
    }

    const changeAvatar = (e) => {
        if ((formRef.current.files[0].type.split('/')[0] === "image")) {
            var reader = new FileReader();
            reader.onload = function () {
                setPreviewAvatar(reader.result);
            }
            reader.readAsDataURL(formRef.current.files[0]);
            
        } else {
            //display error
        }
    }
    
    return (
        <div className="text-center position-relative w-100 h-100">
            <i className="bi bi-x-circle position-absolute top-0 end-0 fa-lg" onClick={exitViewServer}></i>

            {server && permission ?

            <div className="container start-50 translate-middle-x top-0 position-absolute w-100 mt-4 m-1 border border-secondary p-3">
                <div className="row ">
                    <div className="col-md-4 position-relative"  >
                        <div className="position-relative" onMouseOver={mouseOverEvent} onMouseLeave={mouseLeaveEvent} onClick={onClickAvatar}>
                            {hoverOverAvatar ? <img style={{borderRadius:'50%', objectFit:'cover', height:'100px', width:'100px'}} className="thumbnail-image start-50 top-50 translate-middle position-absolute" src={`${process.env.PUBLIC_URL}/images/server_default.png`}/> : ""}
                            <Avatar large={true} localPreview={previewAvatar} avatarPostId={server.avatarPostId}/>
                            <input type="file" onChange={changeAvatar} name="file-upload" id="file" ref={formRef} style={{ display: "none" }}/>
                        </div>
                    </div>
                    <div className="col-md-8">
                        <div className="row">
                            {permission.permissions >= 4 ? 
                                <input type="text" value={editServerName} onInput={e => setEditServerName(e.target.value)} className="form-control w-75" rows="1" placeholder="Server Name" aria-label="Server Name"/>
                            : server.serverName}
                            
                        </div>
                        <div className="row">
                            Server ID: {server._id}
                        </div>
                        <div className="row">
                            {permission.permissions >= 4 ? 
                                <button type="button" onClick={saveChanges} className="mx-1 my-2 text-center w-25 row btn-md btn-outline-primary">Save Changes</button>
                            : ""}
                        </div>
                    </div>
                </div>
              
                    <div className="row">
                       
                    </div>
                
            </div>

            : <Loading/> }
        </div>
    )
}

export default ViewServer;
