import React, {useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
import ServerThumbnail from '../thumbnails/ServerThumbnail';
import axios from 'axios'

function LeftSidebar(props) {

    const [permissions, setPermissions] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:5000/accounts/userServers`, {headers: 
            {
            "x-access-token": localStorage.getItem("authToken")
            }
        })
        .then((res) => {
            setPermissions(res.data);
        });
    }, [props.newServer]);


    //Calls back to App, which will render ServerCreation instead of Main.
    const newServer = () => {
        props.setComponentDisplay(1);
    }

    return(

        <div className="w-100">
            <button type="button" className="w-100 btn btn-outline-primary mt-2" onClick={newServer}><i class="bi bi-plus-circle"></i> Create new server</button>
            <ul className="list-group">
                {permissions.map((el, i) => (
                    <ServerThumbnail permissionObject={el} changeServerCallback={props.changeServerCallback}/>
                ))}
            </ul>
        </div>
    )
    
} 

export default LeftSidebar;
