import React, {useEffect, useState} from 'react';
import './css/LeftSidebar.css'
import ServerThumbnail from './ServerThumbnail';

function LeftSidebar(props) {

    const [serverThumbnails, setServerThumbnails] = useState([]);

    useEffect(() => {
        let newThumbnails = [];
        for (let i = 0; i < 5; i++) {
            newThumbnails.push(
            <li>
                <ServerThumbnail/>
            </li>
            );
        };
        setServerThumbnails([...serverThumbnails, ...newThumbnails])
    }, []);

    //Calls back to App, which will render ServerCreation instead of Main.
    const newServer = () => {
        console.log("clicked");
        props.setComponentDisplay(0);
    }

    return(

        <div className="leftsidebar-wrapper">
            <button type="button" class="btn btn-primary" onClick={newServer}><i class="bi bi-plus-circle"></i> Create new server</button>
            <ul className="list-group">
                {serverThumbnails}
            </ul>
        </div>
    )
    
} 

export default LeftSidebar;
