import React, {useEffect, useState} from 'react';
import './css/ServerThumbnail.css';
import axios from 'axios'

function ProfileThumbnail(props) {

    const [avatarDataId, setAvatarDataId] = useState();

    useEffect(() => {
        axios.get(`http://localhost:5000/account/avatar`, {headers: 
            {
            "x-access-token": localStorage.getItem("authToken")
            }
        })
        .then((res) => {
            if (res.status === 200) {
                setAvatarDataId(res.data.dataId);
            }
        }).catch((err) => {
            console.log(err);
        });
    }, []);

    const clickedThumbnail = (e) => {
        //do nothing yet
    }

    return(
        <div className="container server-thumbnail-wrapper w-90 m-2 border border-secondary" onClick={clickedThumbnail}>
            <div className="row">
                <div className="col-xs my-auto">
                    <img className="server-thumbnail-image" src={avatarDataId ? `http://localhost:5000/files/image/${avatarDataId}` : `${process.env.PUBLIC_URL}/images/user_default.png`}/>
                </div>
                <div className="col my-auto">
                        <div className="row">{props.userInfo.username}</div>
                        <button type="button" className="row w-100 btn btn-outline-primary mt-2"><i class="bi bi-plus-circle"></i>Edit Profile</button>
                </div>
            </div>
        </div>

    );
}

export default ProfileThumbnail;
