import React, {useState} from 'react';
import './css/Login.css'

function Login(props) {
    const [registrationMode, setRegistrationMode] = useState(false);
    const [usernameInput, setUsernameInput] = useState('');
    const [passwordInput, setPasswordInput] = useState('');
    const [confirmPasswordInput, setConfirmPasswordInput] = useState('');
    const [emailInput, setEmailInput] = useState('');
    const [errorText, setErrorText] = useState('');
    
    const toggleRegistrationMode = () => {
        if (!registrationMode) {
        setRegistrationMode(true);
        } else {
            setRegistrationMode(false);
        }
    }

    const handleRegistration = () => {
        if (passwordInput === confirmPasswordInput ){
            const fd = new FormData();
            fd.append('username', usernameInput);
            fd.append('password', passwordInput);
            fd.append('email', emailInput)
        } else {
            setErrorText("Your password does not match.")
        }
    }

    if (!registrationMode){
        return (
            <div className="login text-center">
                <div class="input-group mb-3 ">
                    <input type="text" value={usernameInput} onInput={e => setUsernameInput(e.target.value)} className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"/>
                </div>
                <div class="input-group mb-3">
                    <input type="text" value={passwordInput} onInput={e => setPasswordInput(e.target.value)} className="form-control" placeholder="Password" aria-label="Password" aria-describedby="basic-addon1"/>
                </div>
                <button type="button" className="btn btn-outline-primary">Login</button>
                <div className="user-select-none" onClick={toggleRegistrationMode}>Register new account</div>
            </div>
        )
    } else {
        return (
            <div className="login text-center">
                <div>{errorText}</div>
                <div class="input-group mb-3 ">
                    <input type="text" value={usernameInput} onInput={e => setUsernameInput(e.target.value)} id="reg-username" className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"/>
                </div>
                <div class="input-group mb-3 ">
                    <span className="input-group-text" id="basic-addon1">@</span>
                    <input type="text" value={emailInput} onInput={e => setEmailInput(e.target.value)} className="form-control" placeholder="Email" aria-label="Email" aria-describedby="basic-addon1"/>
                </div>
                <div class="input-group mb-3">
                    <input type="text" value={passwordInput} onInput={e => setPasswordInput(e.target.value)} className="form-control" placeholder="Password" aria-label="Password" aria-describedby="basic-addon1"/>
                </div>
                <div class="input-group mb-3">
                    <input type="text" value={confirmPasswordInput} onInput={e => setConfirmPasswordInput(e.target.value)} className="form-control" placeholder="Confirm Password" aria-label="Confirm Password" aria-describedby="basic-addon1"/>
                </div>
                <button type="button" className="btn btn-outline-primary">Register</button>
                <div className="user-select-none" onClick={toggleRegistrationMode}>Return to login</div>
            </div>
        )
    }
}

export default Login;
