import React, {useEffect, useState} from 'react';
import '../css/Thumbnail.css';
import axios from 'axios'

function Avatar(props) {

    const [avatarDataId, setAvatarDataId] = useState();

    useEffect(() => {
        if (props.avatarPostId != null) {
            axios.get(`http://localhost:5000/files/getImageFromPost/${props.avatarPostId}`, {headers: 
                {
                "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200) {
                    setAvatarDataId(res.data.dataId);
                }
            }).catch((err) => {
                console.log(err);
            });
        }
    }, [props.avatarPostId]);

    const clickedThumbnail = (e) => {
        //do nothing yet
    }

    return(
        <img style="border-radius:50% height:50px width:50px" className="thumbnail-image" src={avatarDataId ? `http://localhost:5000/files/image/${avatarDataId}` : `${process.env.PUBLIC_URL}/images/server_default.png`}/>
    );
}

export default Avatar;
