import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { useNavigate, useParams} from 'react-router-dom';
import {Dropdown, DropdownButton} from 'react-bootstrap';


import UserThumbnail from '../thumbnails/UserThumbnail';
import CommentSection from '../secondary/CommentSection';
import Loading from '../secondary/Loading';
import '../css/ViewPost.css'

function ViewPost(props) {
    
    const [userId, setUserId] = useState('');
    const [post, setPost] = useState({});
    const [displayPostText, setDisplayPostText] = useState('');
    const [editMode, setEditMode] = useState(false);
    const [date, setDate] = useState('');

    const navigate = useNavigate();
    const params = useParams();
  
    useEffect(() => {
        axios.get(`http://localhost:5000/servers/getPost/${params.serverId}/${params.postId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
        }).then((res) => {
            console.log(res.data);
            console.log(new Date(res.data.post.date));
            setDate(new Date(res.data.post.date));
            setPost(res.data.post);
            setUserId(res.data.userId);
            setDisplayPostText(res.data.post.postText);
        })
    }, [params.postId, params.serverId]);

    

    const editPost = () => {
        const fd = new FormData();
        fd.append("displayPostText", displayPostText);

        axios.post(`http://localhost:5000/posts/editPost/${params.serverId}/${params.postId}`, fd,
            {
                headers: 
                    {
                        "x-access-token": localStorage.getItem("authToken"),
                        'Content-Type': 'multipart/form-data'
                    }
            })
            .then((res) => {
                if (res.status === 200){
                    //display confirmation toast maybe
                } else {
                    setDisplayPostText(post.postText);
                }
            });
    }

    const deletePost = () => {
        axios.delete(`http://localhost:5000/posts/deletePost/${params.serverId}/${params.postId}`, {headers: 
        {
            "x-access-token": localStorage.getItem("authToken")
        }})
        .then((res) => {
            if (res.status === 200){
                exitViewPost();
            }
        });
    }

    //Enter pressed in the edit post text field.
    const confirmEdit = (e) => {
        if(e.keyCode === 13 && e.shiftKey === false) {
          e.preventDefault();
          editPost();
          setEditMode(false);
        }
    }

    //Handles the drop down menu for the post.
    const handleSelect = (e) => {
        if (e === "edit"){
            setEditMode(true);
        } else if (e === "delete"){
            deletePost();
        } else if (e === "report"){
            
        }
    }

    //Exit view post mode.
    const exitViewPost = () => {
        navigate(`/s/${params.serverId}`);
    }

   

    return (
        <div className="container text-center position-relative w-100 h-100">
            <i className="bi bi-x-circle position-absolute top-0 end-0 fa-lg" onClick={exitViewPost}></i>

            {post ?

                <div className="row start-50 translate-middle-x top-0 position-absolute w-100 mt-4 m-1">
                    <div className="col">
                        {post.dataType !== "text" ?
                            <div className="row border border-secondary content-viewer">
                                <div className="position-absolute end-0 me-4 mt-2">
                                    <DropdownButton title="" id="dropdown-basic-button" variant="secondary" size="sm" onSelect={handleSelect}>
                                        {(post.authorId === userId) ? <Dropdown.Item eventKey="edit">Edit</Dropdown.Item> : ""}
                                        {(post.authorId === userId) ? <Dropdown.Item eventKey="delete">Delete</Dropdown.Item> : ""}
                                        <Dropdown.Item eventKey="report">Report</Dropdown.Item>
                                    </DropdownButton>
                                </div>
                                <div>
                                    {post.dataType === "image" ? <img alt={`Posted by user with ID ${post.authorId}`}  className="img-fluid my-3 mx-auto d-block" src={`http://localhost:5000/files/image/${post.dataId}`}/> : null}
                                </div>
                            </div>
                        : null} 

                        <div className="row border border-secondary">
                            <div className="col-4 my-auto border-end border-secondary">
                                {/* {date.toDateString()} */}
                            </div>
                            <div className="col-8">
                                <UserThumbnail userId={post.authorId}/>
                            </div>
                        </div>

                        <div className="row border border-secondary content-text mb-4 h-auto">
                            <div className="p-2">
                                {
                                    editMode 
                                    ?
                                    <textarea type="text" value={displayPostText} onInput={e => setDisplayPostText(e.target.value)} className="form-control mt-3" rows="2" placeholder="Edit Post" aria-label="Edit Post" onKeyDown={confirmEdit}/>
                                    :
                                    displayPostText
                                }
                            </div>
                        </div>
                    </div>
                    {userId ?
                        <CommentSection userId={userId} serverId={params.serverId} postId={params.postId}/>
                    : <Loading/>} 
                </div>

            : <Loading/>}
            
        </div>
  );
}

export default ViewPost;
