import React from 'react';
import './css/UploadBar.css';
import {Form} from 'react-bootstrap';
import axios from 'axios';

function UploadBar(props) {

    const fileUpload = () => {
        
    }

    return(
        <div className=" p-1 mt-3">
            <Form action="/file" method="post" encType="multipart/form-data">
                <Form.Group controlId="formFile" className="mb-3" a>
                    <Form.Control type="file" name="file-upload" onChange={(e) => {
                        const fd = new FormData();
                        fd.append('file-upload', e.target.files[0], e.target.files[0].name);
                        axios.post(`http://localhost:5000/file/upload/${props.currentServerId}`, fd, {
                            headers: {
                                "x-access-token": localStorage.getItem("authToken")
                            }
                        })
                        .then((res) => {
                            console.log(res);
                            this.props.newFileCallback(res.data.id);
                        })
                        .catch((err) => {
                            //console.log(err)
                        })
                    }}/>
                </Form.Group>
            </Form>
        </div>
    )
    
} 

export default UploadBar;
