import UploadBar from './UploadBar'
import ImageThumbnail from './ImageThumbnail'
import './css/Main.css'
import axios from 'axios'

function Main(props) {
    console.log("MAIN FUNCTION");
    const [imageIds, setImageIds] = useState([]);

    useEffect(() => {
        if (props.currentServerId != null) {
            fetchData();
        } 
    }, []);

    
     //Callback to display new files when they are uploaded by the user.
     //TODO why is imagethumbnails cleared out when addednewfile is called?
     const addedNewFile = (id) => {
        setI([...imageThumbnails, id]);
    }

    //Retrieve files from the server
    const fetchData = () => {
        axios.get(`http://localhost:5000/servers/getImages/${props.currentServerId}`, {headers: 
            {
                "x-access-token": localStorage.getItem("authToken")
            }
        })
        .then((res) => {
            let newThumbnails = [];
            res.data.map((el, i) => {
                newThumbnails.push(el.dataId);
            });
           
            setImageThumbnails([...imageThumbnails, ...newThumbnails]);

        });
    }

   

    return(
        <div>
            <div className="row gy-4 main-grid mx-auto pt-3">
                {imageThumbnails.map((el, i) => {
                    <ImageThumbnail id={el} key={el}/>
                })}
            </div> 
            <UploadBar newFileCallback={addedNewFile} currentServerId={props.currentServerId}/>
        </div>
    )
} 

export default Main;
