import React from 'react';
import './css/UploadBar.css'

class UploadBar extends React.Component {
    render() {
        return(
            <div class="upload-bar-wrapper border p-1 mt-3 border-secondary row">
                <div class="row" id="input-form-wrapper">
                    <input type="file" accept="image/*, video/*, audio/*"></input>
                </div>
                <div class="col">
                    <button>Submit</button>
                </div>
            </div>
        )
    }
} 

export default UploadBar;
