import React, {useState, useEffect} from 'react';
import axios from 'axios';

import LeftSidebar from './LeftSidebar';
import Main from './Main';
import RightSidebar from './RightSidebar';
import Loading from './Loading';
import Login from './Login';
import ServerCreation from './ServerCreation';
import './css/App.css'


function App(props) {
    const [loading, setLoading] = useState(false);
    const [loggedIn, setLoggedIn] = useState(false);
    const [currentServerId, setCurrentServerId] = useState(null);

    //0 - main
    //1 - create server
    const [componentDisplay, setComponentDisplay] = useState(0);

    useEffect(() => {
        if (!localStorage.getItem("authToken")){
            setLoading(false);
            setLoggedIn(false);
        } else {
            verifyAuth();
        }
    })


    const verifyAuth = () => {
        axios.get('http://localhost:5000/account/verifyAuth', {
            headers: {
                "x-access-token": localStorage.getItem("authToken")
                // "Access-Control-Allow-Origin": "*"
            }
            }).then((res) => {
                if (res.status === 200) {
                    setCurrentServerId(localStorage.getItem("mostRecentServer"));
                    setLoggedIn(true);

                }
            }).catch((err) => {
                localStorage.clear();
            });
    }

    //Log out user
    const logout = () => {
        localStorage.clear();
        setLoading(true);
        setLoggedIn(false);
    }

    //Changes the server the user is viewing and updates cookies.
    const changeServer = (serverId) => {
        localStorage.setItem("mostRecentServer", serverId);
        setCurrentServerId(serverId);
    }


    if (loading) {
        return(
            <div className="wrapper w-100 h-100 mw-100 mh-100">
                <Loading/>
            </div>
        )
    } else if (loggedIn) {

        var component = <div>An error has occured</div>;

        if (componentDisplay === 0 && (currentServerId != null)) {
            console.log("serving MAIN");
            component = <Main className="main" currentServerId={currentServerId}/>
        } else if (componentDisplay === 1) {
            component = <ServerCreation setComponentDisplay={setComponentDisplay} changeServer={changeServer}/>
        }

        return(
            <div className="wrapper w-100 h-100">
                <LeftSidebar className="leftsidebar" setComponentDisplay={setComponentDisplay} changeServerCallback={changeServer}/>
                <div className="center">
                    {component}
                </div>
                <RightSidebar className="rightsidebar"/>
                <button type="button" class="btn btn-outline-primary" onClick={logout}>Log Out</button>
            </div>
        )

    } else {
        return(
            <div className="wrapper w-100 h-100">
                <Login verifyAuth={verifyAuth}/>
            </div>
        )
    }
    
} 

export default App;
