import React, {useState} from 'react';
import '../css/Thumbnail.css';
import {Dropdown, DropdownButton} from 'react-bootstrap';

function User(props) {

    const clickedThumbnail = (e) => {
        //do nothing yet
    }

    const handleSelect = (e) => {
        if (e === "#/delete"){

        } else if (e === "#/report"){

        }
    }

    console.log(`${props.authorId} and ${props.userInfo._id}`);

    return(
        <div className="container thumbnail-wrapper w-90 m-2 border border-secondary" onClick={clickedThumbnail}>
            <div className="position-absolute end-0 me-2">
                <DropdownButton id="dropdown-basic-button" variant="secondary" size="sm" onSelect={handleSelect}>
                    {(props.authorId === props.userInfo._id )? <Dropdown.Item href="#/delete">Delete</Dropdown.Item> : ""}
                <Dropdown.Item href="#/report">Report</Dropdown.Item>
                </DropdownButton>
            </div>
            <div className="row h-100">
                <div className="col-md-2 my-3">
                    <img className="thumbnail-image" src={props.imageDataId ? `http://localhost:5000/files/image/${props.authorAvatarDataId}` : `${process.env.PUBLIC_URL}/images/user_default.png`}/>
                </div>
                <div className="col-md-10 my-auto">
                    <div className="border-bottom border-secondary text-center">
                        <h5>{props.authorName}</h5>
                    </div>
                    <div className="w-100 p-2">
                        {props.commentText}
                    </div>
                </div>
            </div>
        </div>

    );
}

export default User;
