import React, {useEffect, useState} from 'react';
import UserThumbnail from '../thumbnails/UserThumbnail';
import axios from 'axios'
import ProfileThumbnail from '../thumbnails/ProfileThumbnail';

function RightSidebar(props) {

    const [userPermissions, setUserPermissions] = useState([]);

    

    useEffect(() => {
        if (props.currentServerId != null) {
            //These are permission objects it's receiving
            axios.get(`http://localhost:5000/servers/serverUsers/${props.currentServerId}`, {headers: 
                {
                "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200){
                    setUserPermissions(res.data);
                }
            });
        } else {
            //maybe populate it with friends
        }
    }, [props.currentServerId]);


    return(

        <div className="w-100">
            <ul className="list-group">
                <button type="button" className="row btn btn-outline-primary w-100 mt-2">Log out</button>

                <UserThumbnail userId={props.userInfo._id} key={props.userInfo._id} viewProfileCallback={props.viewProfileCallback}/>

                {userPermissions.map((el, i) => (
                    el.userId != props.userInfo._id ?
                    <UserThumbnail userId={el.userId} key={el._id} viewProfileCallback={props.viewProfileCallback}/>
                    : ""
                ))}

            </ul>
        </div>
    )
    
} 

export default RightSidebar;
