import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import Avatar from '../thumbnails/Avatar';
import Loading from '../secondary/Loading';

import '../css/Thumbnail.css';

function ServerThumbnail(props) {

    const navigate = useNavigate();
    const [server, setServer] = useState({});

    useEffect(() => {
        if (props.permissionObject != null) {
            axios.get(`http://localhost:5000/servers/getServer/${props.permissionObject.serverId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200){
                    setServer(res.data); 
                }
            });
        }
    }, []);

