import React, {useEffect, useState} from 'react';
import axios from 'axios';

import CommentThumbnail from '../thumbnails/CommentThumbnail'
import '../css/Thumbnail.css';

function CommentSection(props) {

    
    const [comments, setComments] = useState([]);
    const [updateComments, setUpdateComments] = useState(true);
    const [newComment, setNewComment] = useState('');

    useEffect(() => {
        axios.get(`http://localhost:5000/posts/getComments/${props.serverId}/${props.postId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200){
                    setComments(res.data);  
                }
            });
    }, [updateComments]);

    
    const deleteComment = (commentId) => {
        axios.delete(`http://localhost:5000/posts/deleteComment/${props.serverId}/${commentId}`, {headers: 
        {
            "x-access-token": localStorage.getItem("authToken")
        }})
        .then((res) => {
            if (res.status === 200){
                setUpdateComments(!updateComments);
            }
        });
    }

    const makeComment = (e) => {
        const fd = new FormData();
        fd.append("commentText", newComment);

        axios.post(`http://localhost:5000/posts/makeComment/${props.serverId}/${props.postId}`, fd,
            {
                headers: 
                    {
                        "x-access-token": localStorage.getItem("authToken"),
                        'Content-Type': 'multipart/form-data'
                    }
            })
        .then((res) => {
            if (res.status === 200){
                setNewComment('');
                setUpdateComments(!updateComments);
            }
        });
    }

     //Enter pressed in the comment text field.
     const submitComment = (e) => {
        if(e.keyCode == 13 && e.shiftKey == false) {
          e.preventDefault();
          makeComment(e);

        }
    }

    return(
        <div className="col border border-secondary content-comment mb-4 ">
            <ul class="list-group w-100">
                    <div className="border-bottom border-secondary pb-2">
                    <textarea type="text" value={newComment} onInput={e => setNewComment(e.target.value)} className="form-control mt-3" rows="2" placeholder="Comment" aria-label="Comment" onKeyDown={submitComment}/>
                    <button type="button" class="btn btn-outline-secondary" onClick={makeComment}>Post Comment</button>
                    </div>
                    {comments.map((el, i) => (
                        <CommentThumbnail   deleteCommentCallback={deleteComment}
                                            commentObject={el}/>
                    ))}
            </ul>
        </div>

    );
}

export default CommentSection;
