//Written by Karl Pollock, 2022.
//KarlPollock91@gmail.com
//www.karlpollock.com

//RightSidebar.js is the component that shows all of the servers the user is currently a member of on the left side of the screen.

import React, {useEffect, useState} from 'react';
import UserThumbnail from '../thumbnails/UserThumbnail';
import axios from 'axios'

import * as Constants from '../../utils/constants';

function RightSidebar(props) {

    const [userPermissions, setUserPermissions] = useState([]);

    

    useEffect(() => {
        if (props.currentServerId != null) {
            //These are permission objects it's receiving
            axios.get(`${Constants.SERVER_URL}/servers/serverUsers/${props.currentServerId}`, {headers: 
                {
                "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200){
                    setUserPermissions(res.data);
                }
            });
        } else {
            //maybe populate it with friends
        }
    }, [props.currentServerId]);


    return(

        <div className="w-100">
            <ul className="list-group">
                <button type="button" className="btn btn-outline-primary w-100 mt-2" onClick={props.logoutCallback}>Log out</button>

                <UserThumbnail userId={props.userInfo._id} key={props.userInfo._id}/>

                {userPermissions.map((el, i) => (
                    el.userId !== props.userInfo._id ?
                    <UserThumbnail newUser={props.newUser} userId={el.userId} key={el._id}/>
                    : ""
                ))}

            </ul>
        </div>
    )
    
} 

export default RightSidebar;
