import React, {useState} from 'react';
import axios from 'axios';
import {Form} from 'react-bootstrap';
import Avatar from '../thumbnails/Avatar';
import { useNavigate } from 'react-router-dom';

//This is going to be a more general profile section. Username/Avatar changing, managing list of servers. 
//We'll add a seperate account section for editing email address and whatever else comes up.

function ViewProfile(props) {
    
    const navigate = useNavigate();
    const [profileObject, setProfileObject] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:5000/accounts/getUser/${params.userId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
        }).then((res) => {
            setPost(res.data.post);
            setUserId(res.data.userId);
            setDisplayPostText(res.data.post.postText);
            date = new Date(req.data.post.date);
        })
    }, []);

     //Exit view post mode.
     const exitViewProfile = () => {
        navigate(-1);
    }
    
    return (
        <div className="text-center position-relative w-100 h-100">
            <i className="bi bi-x-circle position-absolute top-0 end-0 fa-lg" onClick={exitViewProfile}></i>

            {profileObject ?

            <div className="container start-50 translate-middle-x top-0 position-absolute w-100 mt-4 m-1 border border-secondary p-3">
                <div className="row ">
                    <div className="col-md-4">
                        <Avatar avatarPostId={props.profileObject.avatarPostId}/>
                    </div>
                    <div className="col-md-8">
                        <div className="row">
                            {props.profileObject.username}
                        </div>
                        <div className="row">
                            Account ID: {props.profileObject._id}
                        </div>
                    </div>
                </div>
                {props.profileObject._id === props.userInfo._id ? 
                    <div className="row">
                        <div className="col-md">
                            <button type="button" className="row btn-md btn-outline-primary">Save Changes</button>
                        </div>
                    </div>
                : ""}
            </div>

            : <Loading/> }
        </div>
    )
}

export default ViewProfile;
