import React, {useEffect, useState} from 'react';
import '../css/Thumbnail.css';
import axios from 'axios';
import Avatar from '../thumbnails/Avatar';

function UserThumbnail(props) {

    const [user, setUser] = useState({});

    useEffect(() => {
        if (props.permissionsObject != null) {
            //These are permission objects it's receiving
            axios.get(`http://localhost:5000/accounts/getUser/${props.permissionsObject.userId}`, {headers: 
                {
                "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200){
                    setUser(res.data);
                    console.log("USER");
                    console.log(res.data);
                }
            });
        } else {
            //maybe populate it with friends
        }
    }, []);

    const clickedThumbnail = (e) => {
        //do nothing yet
    }

    return(
        <div className="container thumbnail-wrapper w-90 m-2 border border-secondary" onClick={clickedThumbnail}>
            <div className="row h-100">
                <div className="col-md-4 my-auto">
                    <Avatar avatarPostId={user.avatarPostId}/>
                </div>
                <div className="col-md-8 my-auto">
                    {user.username}
                </div>
            </div>
        </div>

    );
}

export default UserThumbnail;
