import React, {useState} from 'react';
import axios from 'axios';
import {Form} from 'react-bootstrap';

import './css/ViewPost.css'

function ViewPost(props) {

    const [content, setContent] = useState();
    const [text, setText] = useState();

    const exitViewPost = () => {
        props.setComponentDisplay(0);
    }

    return (
        <div className="container text-center position-relative ">
            <i className="bi bi-x-circle position-absolute top-0 end-0 fa-lg" onClick={exitViewPost}></i>
            <div className="mt-5">
                <div className="border border-secondary content-viewer">
                    test
                    {content}
                </div>
                <div className="border border-secondary content-text">
                    testa
                    {text}
                </div>
            </div>
        </div>
  );
}

export default ViewPost;
