import React, {useState} from 'react';
import '../css/Thumbnail.css';

function User(props) {

    const clickedThumbnail = (e) => {
        //do nothing yet
    }

    return(
        <div className="container thumbnail-wrapper w-90 m-2 border border-secondary" onClick={clickedThumbnail}>
            <div class="dropdown position-absolute top-0 end-0">
                <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"/>
                    <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                        <a class="dropdown-item" href="#">Delete</a>
                        <a class="dropdown-item" href="#">Report</a>
                    </div>
            </div>
            <div className="row h-100">
                <div className="col-md-2 my-3">
                    <img className="thumbnail-image" src={props.imageDataId ? `http://localhost:5000/files/image/${props.imageDataId}` : `${process.env.PUBLIC_URL}/images/user_default.png`}/>
                </div>
                <div className="col-md-10 my-auto">
                    <div className="border-bottom border-secondary text-center">
                        <h5>{props.username}</h5>
                    </div>
                    <div className="w-100 p-2">
                        {props.commentText}
                    </div>
                </div>
            </div>
        </div>

    );
}

export default User;
