import React from 'react';
import './css/UploadBar.css'

class UploadBar extends React.Component {
    render() {
        return(
            <div className="upload-bar-wrapper border p-1 mt-3 border-secondary">
                <input className="text-center" type="file" accept="image/*, video/*, audio/*"></input>
            </div>
        )
    }
} 

export default UploadBar;
