import React, {useEffect, useState} from 'react';
import axios from 'axios';
import '../css/Thumbnail.css';
import Avatar from '../thumbnails/Avatar';

function ServerThumbnail(props) {

    
    const [server, setServer] = useState({});

    useEffect(() => {
        console.log("SERVER THUMBNAIL USE EFFECT");
        console.log(props.permissionObject);
        if (props.permissionObject != null) {
            axios.get(`http://localhost:5000/servers/getServer/${props.permissionObject.serverId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200){
                    console.log(`Set server`);
                    setServer(res.data); 
                }
            });
        }
    }, []);

    const clickedThumbnail = (e) => {
        console.log(`clicked thumbnail and sending ${server._id}`);
        props.changeServerCallback(server._id);
    }

    return(
        <div className="thumbnail-wrapper w-90 m-2 border border-secondary row" onClick={clickedThumbnail}>
            <div className="col my-auto">
                <Avatar avatarPostId={server.avatarPostId}/>
            </div>
            <div className="thumbnail-name col my-auto">
                {server.serverName}
            </div>
        </div>

    );
}

export default ServerThumbnail;
