import React, {useEffect, useState} from 'react';
import ServerThumbnail from './ServerThumbnail';
import axios from 'axios'

function LeftSidebar(props) {

    const [userServers, setUserServers] = useState([]);

    

    useEffect(() => {
        axios.get(`http://localhost:5000/account/userServers`, {headers: 
            {
            "x-access-token": localStorage.getItem("authToken")
            }
        })
        .then((res) => {
            setUserServers([...userServers, ...res.data]);
        });
    }, []);


    //Calls back to App, which will render ServerCreation instead of Main.
    const newServer = () => {
        props.setComponentDisplay(1);
    }

    return(

        <div className="w-100">
            <button type="button" className="w-100 btn btn-outline-primary mt-2" onClick={newServer}><i class="bi bi-plus-circle"></i> Create new server</button>
            <ul className="list-group">
                {userServers.map((el, i) => (
                    <ServerThumbnail serverId={el._id} serverName={el.serverName} imageDataId={el.imageDataId} key={el._id} changeServerCallback={props.changeServerCallback}/>
                ))}
            </ul>
        </div>
    )
    
} 

export default LeftSidebar;
