import React, {useState, useEffect} from 'react';
import axios from 'axios';
import Avatar from '../thumbnails/Avatar';
import Loading from '../secondary/Loading'
import {useNavigate, useParams} from 'react-router-dom';

//This is going to be a more general profile section. Username/Avatar changing, managing list of servers. 
//We'll add a seperate account section for editing email address and whatever else comes up.

function ViewProfile(props) {
    
    const [profile, setProfile] = useState(null);
    const [userId, setUserId] = useState();

    const [editUsername, setEditUsername] = useState();

    const navigate = useNavigate();
    const params = useParams();

    useEffect(() => {
        axios.get(`http://localhost:5000/accounts/getUser/${params.userId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
        }).then((res) => {
            setProfile(res.data.profileObject);
            setUserId(res.data.userId);
            setEditUsername(res.data.profileObject.username);
        })
    }, []);

     //Exit view post mode.
     const exitViewProfile = () => {
        navigate(-1);
    }
    
    return (
        <div className="text-center position-relative w-100 h-100">
            <i className="bi bi-x-circle position-absolute top-0 end-0 fa-lg" onClick={exitViewProfile}></i>

            {profile ?

            <div className="container start-50 translate-middle-x top-0 position-absolute w-100 mt-4 m-1 border border-secondary p-3">
                <div className="row ">
                    <div className="col-md-4">
                        <Avatar large={true} avatarPostId={profile.avatarPostId}/>
                    </div>
                    <div className="col-md-8">
                        <div className="row">
                            {profile._id  === userId ? 
                                <input type="text" value={editUsername} onInput={e => setEditUsername(e.target.value)} className="form-control" rows="1" placeholder="Username" aria-label="Username"/>
                            : profile.username}
                            
                        </div>
                        <div className="row">
                            Account ID: {profile._id}
                        </div>
                        <div className="row">
                            {profile._id === userId ? 
                                <button type="button" className="text-center mx-auto w-25 row btn-md btn-outline-primary">Save Changes</button>
                            : ""}
                        </div>
                    </div>
                </div>
              
                    <div className="row">
                       
                    </div>
                
            </div>

            : <Loading/> }
        </div>
    )
}

export default ViewProfile;
