import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {Form} from 'react-bootstrap';

import './css/ViewPost.css'

function ViewPost(props) {

    const [content, setContent] = useState();
    const [text, setText] = useState();

    const [newComment, setNewComment] = useState('');

    useEffect(() => {

    }, []);

    const exitViewPost = () => {
        props.setComponentDisplay(0);
    }

    const submitComment = (e) => {
        if(e.keyCode == 13 && e.shiftKey == false) {
          e.preventDefault();
        }
      }

    return (
        <div className="container text-center position-relative w-100 h-100">
            <i className="bi bi-x-circle position-absolute top-0 end-0 fa-lg" onClick={exitViewPost}></i>
            <div className="row start-50 translate-middle-x top-0 position-absolute w-75 mt-4">
                <div className="col">
                    {props.postObject.dataType != "text" ?
                        <div className="row border border-secondary content-viewer">
                            <div>
                                {props.postObject.dataType === "image" ? <img className="img-fluid my-3 mx-auto d-block" src={`http://localhost:5000/files/image/${props.postObject.dataId}`}/> : null}
                            </div>
                        </div>
                    : null} 
                    <div className="row border border-secondary content-text mb-4">
                        {props.postObject.postText}
                    </div>
                </div>
                <div className="col border border-secondary content-comment mb-4">
                    <ul class="list-group w-100">
                        <div className="border-bottom border-secondary pb-2">
                        <textarea type="text" value={newComment} onInput={e => setNewComment(e.target.value)} className="form-control mt-3" rows="2" placeholder="Comment" aria-label="Comment" onKeyDown={submitComment}/>
                        <button type="button" class="btn btn-outline-secondary">Post Comment</button>
                        </div>
                        
                    </ul>
                </div>
            </div>
            
        </div>
  );
}

export default ViewPost;
