import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {Form} from 'react-bootstrap';
import {Dropdown, DropdownButton} from 'react-bootstrap';

import CommentThumbnail from '../thumbnails/CommentThumbnail';
import UserThumbnail from '../thumbnails/UserThumbnail';
import '../css/ViewPost.css'

function ViewPost(props) {
    
    const [comments, setComments] = useState([]);
    const [updateComments, setUpdateComments] = useState(true);
    const [newComment, setNewComment] = useState('');

    const [editMode, setEditMode] = useState(false);
    const [editText, setEditText] = useState(props.postObject.postText);
    
    var date = new Date(props.postObject.date);;

    useEffect(() => {
        if (props.postObject != null) {
            axios.get(`http://localhost:5000/posts/getComments/${props.postObject.serverId}/${props.postObject._id}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200){
                    setComments(res.data);  
                }
            });
        }
    }, [updateComments]);

    const deleteComment = (commentId) => {
        axios.delete(`http://localhost:5000/posts/deleteComment/${props.postObject.serverId}/${commentId}`, {headers: 
        {
            "x-access-token": localStorage.getItem("authToken")
        }})
        .then((res) => {
            if (res.status === 200){
                setUpdateComments(!updateComments);
            }
        });
    }

    const makeComment = (e) => {
        const fd = new FormData();
        fd.append("commentText", newComment);

        axios.post(`http://localhost:5000/posts/makeComment/${props.postObject.serverId}/${props.postObject._id}`, fd,
            {
                headers: 
                    {
                        "x-access-token": localStorage.getItem("authToken"),
                        'Content-Type': 'multipart/form-data'
                    }
            })
            .then((res) => {
                if (res.status === 200){
                    setNewComment('');
                    setUpdateComments(!updateComments);
                }
            });
    }

    
    const exitViewPost = () => {
        props.setComponentDisplay(0);
    }

    const submitComment = (e) => {
        if(e.keyCode == 13 && e.shiftKey == false) {
          e.preventDefault();
          makeComment(e);

        }
    }

    const confirmEdit = (e) => {
        if(e.keyCode == 13 && e.shiftKey == false) {
          e.preventDefault();
          setEditMode(false);
        }
    }

    const handleSelect = (e) => {
        if (e === "edit"){
            setEditMode(true);
        } else if (e === "delete"){
            
        } else if (e === "report"){
            
        }
    }

    return (
        <div className="container text-center position-relative w-100 h-100">
            <i className="bi bi-x-circle position-absolute top-0 end-0 fa-lg" onClick={exitViewPost}></i>
            <div className="row start-50 translate-middle-x top-0 position-absolute w-100 mt-4 m-1">
                <div className="col">
                    {props.postObject.dataType != "text" ?
                        <div className="row border border-secondary content-viewer">
                            <div className="position-absolute end-0 me-4 mt-2">
                                <DropdownButton title="" id="dropdown-basic-button" variant="secondary" size="sm" onSelect={handleSelect}>
                                    {(props.postObject.authorId === props.userInfo._id) ? <Dropdown.Item eventKey="edit">Edit</Dropdown.Item> : ""}
                                    {(props.postObject.authorId === props.userInfo._id) ? <Dropdown.Item eventKey="delete">Delete</Dropdown.Item> : ""}
                                    <Dropdown.Item eventKey="report">Report</Dropdown.Item>
                                </DropdownButton>
                            </div>
                            <div>
                                {props.postObject.dataType === "image" ? <img className="img-fluid my-3 mx-auto d-block" src={`http://localhost:5000/files/image/${props.postObject.dataId}`}/> : null}
                            </div>
                        </div>
                    : null} 

                    <div className="row border border-secondary">
                        <div className="col-4 my-auto border-end border-secondary">
                            {date.toDateString()}
                        </div>
                        <div className="col-8">
                            <UserThumbnail userId={props.postObject.authorId}/>
                        </div>
                    </div>

                    <div className="row border border-secondary content-text h-auto">
                        {
                            editMode 
                            ?
                            <textarea type="text" value={newComment} onInput={e => setEditText(e.target.value)} className="form-control mt-3" rows="2" placeholder="Edit Post" aria-label="Edit Post" onKeyDown={confirmEdit}/>
                            :
                            props.postObject.postText
                        }
                        
                    </div>
                </div>
                <div className="col border border-secondary content-comment mb-4 ">
                    <ul class="list-group w-100">
                        <div className="border-bottom border-secondary pb-2">
                        <textarea type="text" value={newComment} onInput={e => setNewComment(e.target.value)} className="form-control mt-3" rows="2" placeholder="Comment" aria-label="Comment" onKeyDown={submitComment}/>
                        <button type="button" class="btn btn-outline-secondary" onClick={makeComment}>Post Comment</button>
                        </div>
                        {comments.map((el, i) => (
                            <CommentThumbnail   deleteCommentCallback={deleteComment}
                                                commentObject={el}
                                                userInfo={props.userInfo}/>
                        ))}
                        
                    </ul>
                </div>
            </div>
            
        </div>
  );
}

export default ViewPost;
