import React, {useState, useEffect} from 'react';
import axios from 'axios';

import LeftSidebar from './LeftSidebar';
import Main from './Main';
import RightSidebar from './RightSidebar';
import Loading from './Loading';
import Login from './Login';
import ServerCreation from './ServerCreation';
import './css/App.css'


function App(props) {
    const [loading, setLoading] = useState(false);
    const [loggedIn, setLoggedIn] = useState(false);
    //0 - main
    //1 - create server
    const [componentDisplay, setComponentDisplay] = useState(0);

    //TODO figure out why verifyAuth is triggering twice

    const verifyAuth = () => {
        axios.get('http://localhost:5000/account/verifyAuth', {
            headers: {
                "x-access-token": localStorage.getItem("authToken")
                // "Access-Control-Allow-Origin": "*"
            }
            }).then((res) => {
                if (res.status === 200) {
                    setLoggedIn(true);
                }
            }).catch((err) => {
                console.log(err.response);
            });
    }

    useEffect(() => {
        if (!localStorage.getItem("authToken")){
            setLoading(false);
            setLoggedIn(false);
        } else {
            verifyAuth();
        }
    })



    if (loading) {
        return(
            <div className="wrapper w-100 h-100 mw-100 mh-100">
                <Loading/>
            </div>
        )
    } else if (loggedIn) {

        var component = <div>An error has occured</div>;
        console.log(componentDisplay);

        if (componentDisplay === 0) {
            component = <Main className="main"D/>
        } else if (componentDisplay === 1) {
            component = <ServerCreation setComponentDisplay={setComponentDisplay}/>
        }

        return(
            <div className="wrapper w-100 h-100">
                <LeftSidebar className="leftsidebar" setComponentDisplay={setComponentDisplay}/>
                <div className="center">
                    {component}
                </div>
                <RightSidebar className="rightsidebar"/>
                <div>Log Out</div>
            </div>
        )

    } else {
        return(
            <div className="wrapper w-100 h-100">
                <Login verifyAuth={verifyAuth}/>
            </div>
        )
    }
    
} 

export default App;
