import React, {useState, useEffect} from 'react';
import { useParams } from "react-router-dom";
import axios from 'axios'

import PostThumbnail from '../thumbnails/PostThumbnail'
import '../css/Main.css'
import NewPostThumbnail from '../thumbnails/NewPostThumbnail';


function Main(props) {
    const [postIds, setPostIds] = useState([]);
    const params = useParams();

    useEffect(() => {
        if (params.currentServerId != null) {
            axios.get(`http://localhost:5000/servers/getPosts/${params.currentServerId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200) {
                    setPostIds(res.data);
                }
            });
        } 
    }, [params.currentServerId]);

    
     //Callback to display new files when they are uploaded by the user.
     //TODO why is imagethumbnails cleared out when addednewfile is called?
     const addedNewFile = (id) => {
        setPostIds([...postIds, id]);
    }

    return(
        <div className="container">
            <div className="row gy-4 mx-auto pt-3">
                <NewPostThumbnail enterPostCreation={props.enterPostCreation}/>
                {postIds.map((el, i) => (
                    <PostThumbnail postObject={el} key={el._id} viewPost={props.viewPost}/>
                ))}
            </div> 
        </div>
    )
} 

export default Main;
