import React, {useState} from 'react';
import axios from 'axios';
import {Form} from 'react-bootstrap';

import './css/InputForm.css'

function ServerCreation(props) {
    const [nameInput, setNameInput] = useState('');
    const [errorText, setErrorText] = useState('');
    const [formRef, setFormRef] = useState(React.createRef());


    const handleCreation = () => {
        const fd = new FormData();

        fd.append("serverName", nameInput);
        
        if (formRef.current.files[0] && formRef.current.files[0].name) {
            fd.append('file-upload', formRef.current.files[0], formRef.current.files[0].name);
        }

        axios.post(`http://localhost:5000/servers/create`, fd, {
                headers: {
                    'x-access-token': localStorage.getItem("authToken"),
                    'Content-Type': 'multipart/form-data'
                }
            })
        .then((res) => {
            if (res.status === 200) {
                setErrorText("Server created")
                exitServerCreation(res.data.serverId);
            } 
        })
        .catch((err) => {
            setErrorText("Something went wrong.")
        });
    
    }

    const exitServerCreation = (serverId) => {
        props.setComponentDisplay(0);
        props.changeServer(serverId)
    }
        return (
            <div className="input-form-wrapper text-center">
                <i className="bi bi-x-circle position-absolute top-0 end-0 fa-lg" onClick={exitServerCreation}></i>
                <div className="mt-5">
                    <div>{errorText}</div>
                    <div class="input-group mb-3 ">
                        <input type="text" value={nameInput} onInput={e => setNameInput(e.target.value)} className="form-control" placeholder="Server Name" aria-label="servername" aria-describedby="basic-addon1"/>
                    </div>
                    <Form action="/file" method="post" encType="multipart/form-data">
                        <Form.Group controlId="formFile" className="mb-3" a>
                            <Form.Control type="file" name="file-upload" ref={formRef}/>
                        </Form.Group>
                    </Form>
                    <button type="button" onClick={handleCreation} className="btn btn-outline-primary">Create</button>
                </div>
            </div>
        )
}

export default ServerCreation;
