    }

    return(
        <div className="container thumbnail-wrapper w-90">
           
            <div className="row h-100">
                <div className="col-md-4 my-auto">
                    <Avatar avatarPostId={user.avatarPostId}/>
                </div>
                <div className="col-md-6 my-auto">
                    {user.username}
                </div>
                <div className="col-md-2">
                    <DropdownButton className="end-0" title="" id="dropdown-basic-button" variant="secondary" size="sm" onSelect={handleSelect}>
                        <Dropdown.Item eventKey="report">Report</Dropdown.Item>
                    </DropdownButton>
                </div>
            </div>
        </div>

    );
}

export default UserThumbnail;
