import React, {useEffect, useState} from 'react';
import './css/LeftSidebar.css'
import ServerThumbnail from './ServerThumbnail';
import axios from 'axios'

function LeftSidebar(props) {

    const [serverThumbnails, setServerThumbnails] = useState([]);

    

    useEffect(() => {
        axios.get(`http://localhost:5000/account/userServers`, {headers: 
            {
            "x-access-token": localStorage.getItem("authToken")
            }
        })
        .then((res) => {
            let addedThumbnails = new Array();
            res.data.map((el, i) => {
                addedThumbnails.push(<ServerThumbnail serverId={el._id} serverName={el.serverName} thumbnailId={el.thumbnailId} key={el._id} changeServerCallback={props.changeServerCallback}/>)
            })
            setServerThumbnails([...serverThumbnails, ...addedThumbnails]);
        });
    }, []);


    //Calls back to App, which will render ServerCreation instead of Main.
    const newServer = () => {
        props.setComponentDisplay(1);
    }

    return(

        <div className="leftsidebar-wrapper">
            <button type="button" class="btn btn-outline-primary" onClick={newServer}><i class="bi bi-plus-circle"></i> Create new server</button>
            <ul className="list-group">
                {serverThumbnails}
            </ul>
        </div>
    )
    
} 

export default LeftSidebar;
