import React from 'react';
import './css/UploadBar.css';
import {Form} from 'react-bootstrap';
import axios from 'axios';

class UploadBar extends React.Component {
    render() {
        return(
            <div className=" p-1 mt-3">
                <Form id="testid" action="/file" method="post" enctype="multipart/form-data">
                    <Form.Group controlId="formFile" className="mb-3" a>
                        <Form.Control type="file" name="avatar" onchange={(e) => {
                            document.getElementById('testid').submit();
                        }}/>
                    </Form.Group>
                </Form>
            </div>
        )
    }
} 

export default UploadBar;
