import React, {useState} from 'react';
import axios from 'axios';
import {Form} from 'react-bootstrap';

import './css/InputForm.css'

function Login(props) {
    const [nameInput, setNameInput] = useState('');
    const [errorText, setErrorText] = useState('');
    const [formRef, setFormRef] = useState(React.createRef());


    const handleCreation = () => {

        const data = JSON.stringify({serverName: nameInput});

        const fd = new FormData();

        fd.append("document", data);
        fd.append('file-upload', formRef.files[0], formRef.files[0].name);
        
        axios.post('http://localhost:5000/account/register', data, {
            headers: {
                'Content-Type': 'application/json'}
            })
        .then((res) => {
            if (res.status === 200) {
                setErrorText("Server created")
            } 
        })
        .catch((err) => {
            setErrorText("Something went wrong.")
        });
    
    }
        return (
            <div className="input-form-wrapper text-center">
                <i class="bi bi-x-circle position-absolute top-0 end-0 fa-lg pb-5"></i>
                <div>{errorText}</div>
                <div class="input-group mb-3 ">
                    <input type="text" value={nameInput} onInput={e => setNameInput(e.target.value)} className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"/>
                </div>
                <Form action="/file" method="post" encType="multipart/form-data">
                    <Form.Group controlId="formFile" className="mb-3" a>
                        <Form.Control type="file" name="file-upload" ref={formRef}/>
                    </Form.Group>
                </Form>
                <button type="button" onClick={handleCreation} className="btn btn-outline-primary">Create</button>
            </div>
        )
}

export default Login;
