import React, {useEffect, useState} from 'react';
import {Dropdown, DropdownButton} from 'react-bootstrap';
import axios from 'axios';

import Loading from '../secondary/Loading';
import Avatar from '../thumbnails/Avatar';
import '../css/Thumbnail.css';

function CommentThumbnail(props) {

    const [author, setAuthor] = useState(null);
    const [userId, setUserId] = useState();

    useEffect(() => {
        if (props.commentObject != null) {
            axios.get(`http://localhost:5000/accounts/getUser/${props.commentObject.authorId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200){
                    setAuthor(res.data.profileObject); 
                    setUserId(res.data.userId);
                }
            });
        }
    }, [props.commentObject]);

    const clickedThumbnail = (e) => {
        //do nothing yet
    }

    const handleSelect = (e) => {
        if (e === "delete"){
            props.deleteCommentCallback(props.commentObject._id);
        } else if (e === "report"){
            
        }
    }

    return(
        author ?
            <div className="container thumbnail-wrapper w-90 m-2 border border-secondary" onClick={clickedThumbnail}>
                    <div className="position-absolute end-0 me-2">
                        <DropdownButton title="" id="dropdown-basic-button" variant="secondary" size="sm" onSelect={handleSelect}>
                            {(author._id === userId) ? <Dropdown.Item eventKey="delete">Delete</Dropdown.Item> : ""}
                            <Dropdown.Item eventKey="report">Report</Dropdown.Item>
                        </DropdownButton>
                    </div>
                    <div className="row h-100">
                        <div className="col-md-2 my-3">
                            <Avatar avatarPostId={author.avatarPostId}/>
                        </div>
                        <div className="col-md-10 my-auto">
                            <div className="border-bottom border-secondary text-center">
                                <h5>{author.username}</h5>
                            </div>
                            <div className="w-100 p-2">
                                {props.commentObject.commentText}
                            </div>
                        </div>
                    </div>
            </div>
        : <Loading/>

    );
}

export default CommentThumbnail;
