import React, {useEffect, useState} from 'react';
import '../css/Thumbnail.css';
import axios from 'axios'

function ProfileThumbnail(props) {

    const [avatarDataId, setAvatarDataId] = useState();

    useEffect(() => {
        axios.get(`http://localhost:5000/files/getImageFromPost/${props.userInfo.avatarPostId}`, {headers: 
            {
            "x-access-token": localStorage.getItem("authToken")
            }
        })
        .then((res) => {
            if (res.status === 200) {
                setAvatarDataId(res.data.dataId);
            }
        }).catch((err) => {
            console.log(err);
        });
    }, []);

    const clickedThumbnail = (e) => {
        //do nothing yet
    }

    return(
        <div className="container thumbnail-wrapper w-90 m-2 border border-secondary" onClick={clickedThumbnail}>
            <div className="row h-100">
                <div className="col-md-6 my-auto">
                    <Avatar avatarPostId={props.userInfo.avatarPostId}/>
                </div>
                <div className="col-md-6 my-auto">
                        <div className="row text-center">{props.userInfo.username}</div>
                        <button type="button" className="row btn-sm btn-outline-primary">Edit Profile</button>
                </div>
            </div>
        </div>

    );
}

export default ProfileThumbnail;
