import React, {useState, useEffect} from 'react';
import axios from 'axios';

import LeftSidebar from './LeftSidebar';
import Main from './Main';
import RightSidebar from './RightSidebar';
import Loading from './Loading';
import Login from './Login';
import './css/App.css'


function App(props) {
    const [loading, setLoading] = useState(false);
    const [loggedIn, setLoggedIn] = useState(false);

    //TODO figure out why verifyAuth is triggering twice

    const verifyAuth = () => {
        axios.get('http://localhost:5000/account/verifyAuth', {
            headers: {
                "x-access-token": localStorage.getItem("authToken")
                // "Access-Control-Allow-Origin": "*"
            }
            }).then((res) => {
                if (res.status === 200) {
                    setLoggedIn(true);
                }
            }).catch((err) => {
                console.log(err);
            });
    }

    useEffect(() => {
        if (!localStorage.getItem("authToken")){
            console.log("not logged in");
            setLoading(false);
            setLoggedIn(false);
        } else {
            verifyAuth();
        }
    })


    if (loading) {
        return(
            <div className="wrapper w-100 h-100 mw-100 mh-100">
                <Loading/>
            </div>
        )
    } else if (loggedIn) {
        return(
            <div className="wrapper w-100 h-100">
                <LeftSidebar className="leftsidebar"/>
                <div className="center">
                    <Main className="main"/>
                </div>
                <RightSidebar className="rightsidebar"/>
            </div>
        )
    } else {
        return(
            <div className="wrapper w-100 h-100">
                <Login verifyAuth={verifyAuth}/>
            </div>
        )
    }
    
} 

export default App;
