import React, {useState, useEffect, useRef} from 'react';
import {Tabs, Tab, Form, Row, Col} from 'react-bootstrap';
import axios from 'axios';
import Avatar from '../thumbnails/Avatar';
import Loading from '../secondary/Loading'
import {useNavigate, useParams} from 'react-router-dom';
import UserThumbnail from '../thumbnails/UserThumbnail';

function ViewServer(props) {
    
    const [updateServer, setUpdateServer] = useState(false);

    const [server, setServer] = useState(null);
    
    //Permission of the current user in relation to the server.
    const [permission, setPermission] = useState();

    const [madeChanges, setMadeChanges] = useState(true);

    const [editServerName, setEditServerName] = useState('');

    const [hoverOverAvatar, setHoverOverAvatar] = useState(false);
    const [previewAvatar, setPreviewAvatar] = useState();

    const [serverUsers, setServerUsers] = useState(null);
    const [loadedUsers, setLoadedUsers] = useState(false);

    const [loadedSecurities, setLoadedSecurities] = useState(false);

    //Radio menu variables
    const [postsVisibility, setPostsVisibility] = useState();
    const [allowJoin, setAllowJoin] = useState();
    const [allowComment, setAllowComment] = useState();
    const [usersVisibility, setUsersVisibility] = useState();


    const navigate = useNavigate();
    const params = useParams();
    const formRef = useRef(null);

    //UseEffect for retrieving the server information.
    useEffect(() => {
        axios.get(`http://localhost:5000/servers/getServer/${params.serverId}`, {headers: 
                {
                    "x-access-token": localStorage.getItem("authToken")
                }
        }).then((res) => {
            console.log(res.data);
            setServer(res.data.serverObject);
            setPermission(res.data.userId);
            setEditServerName(res.data.serverObject.serverName);
            //Security objects will be available publically with getserver
        });
    }, [params.serverId, updateServer]);

    //UseEffect for getting the list of members of the server.
    useEffect(() => {
       axios.get(`http://localhost:5000/servers/serverUsers/${params.serverId}`, {headers: 
            {
                "x-access-token": localStorage.getItem("authToken")
            }
        }).then((res) => {
            setServerUsers(res.data);
            setLoadedUsers(true);
        })
    }, [params.serverId]);

     //UseEffect for getting the server securities
     useEffect(() => {
        axios.get(`http://localhost:5000/servers/serverSecurities/${params.serverId}`, {headers: 
             {
                 "x-access-token": localStorage.getItem("authToken")
             }
         }).then((res) => {

             setPostsVisibility(res.data.postsVisibility);
             setAllowJoin(res.data.allowJoin);
             setAllowComment(res.data.allowComment);
             setUsersVisibility(res.data.usersVisibility);

             setLoadedSecurities(true);
         })
     }, [params.serverId]);

    //Save changes made to the server.
    const saveChanges = () => {
        const fd = new FormData();

        fd.append("editServerName", editServerName);
        
        fd.append("serverSecurities", {
            postsVisibility: poistsVisibility,
            allowJoin: allowJoin,
            allowComment: allowComment,
            usersVisibility: usersVisibility
        });

        if (formRef.current.files[0] && formRef.current.files[0].name) {
            fd.append('file-upload', formRef.current.files[0], formRef.current.files[0].name);
        }

        if ((!formRef.current.files[0]) || (formRef.current.files[0].type.split('/')[0] === "image")) {
            axios.post(`http://localhost:5000/servers/updateServer/${server._id}`, fd, {
                headers: {
                    'x-access-token': localStorage.getItem("authToken"),
                    'Content-Type': 'multipart/form-data'
                }
            })
            .then((res) => {
                if (res.status === 200) {
                    setUpdateServer(!updateServer);
                    setMadeChanges(false);
                } 
            })
            .catch((err) => {
                console.log(err);
            });
        } else {
            //err
        };

       
    }

     //Exit view post mode.
     const exitViewServer = () => {
        navigate(-1);
    }

    const mouseOverEvent = (e) => {
        if (permission >= 5) {
            setHoverOverAvatar(true);
        }
    }

    const mouseLeaveEvent = (e) => {
        if (permission >= 5) {
            setHoverOverAvatar(false);
        }
    }

    const onClickAvatar = (e) => {
        if (permission >= 5) {
            formRef.current.click();
        }
    }

    const changeAvatar = (e) => {
        if ((formRef.current.files[0].type.split('/')[0] === "image")) {
            var reader = new FileReader();
            reader.onload = function () {
                setPreviewAvatar(reader.result);
            }
            reader.readAsDataURL(formRef.current.files[0]);
            setMadeChanges(true);
            
        } else {
            //display error
        }
    }

    //Triggers onchange in serverName field.
    const serverNameEvent = (e) => {
        setEditServerName(e.target.value)
        setMadeChanges(true);
    }

    const securitySettingEvent = (e) => {
        setMadeChanges(true);
        const updatedSecurities = serverSecurities; 
        switch (e.target.id) {
            case ('security-radio-0-0'):
                updatedSecurities.postsVisibility = 0;
                break;
            case ('security-radio-0-1'):
                updatedSecurities.postsVisibility = 1;
                break;
            case ('security-radio-1-0'):
                updatedSecurities.allowJoin = 0;
                break;
            case ('security-radio-1-1'):
                updatedSecurities.allowJoin = 1;
                break;
            case ('security-radio-1-2'):
                updatedSecurities.allowJoin = 2;
                break;
            case ('security-radio-2-0'):
                updatedSecurities.allowComment = 0;
                break;
            case ('security-radio-2-1'):
                updatedSecurities.allowComment = 1;
                break;
            case ('security-radio-3-0'):
                updatedSecurities.usersVisibility = 0;
                break;
            case ('security-radio-3-1'):
                updatedSecurities.usersVisibility = 1;
                break;
        }
        setServerSecurities(updatedSecurities);
    }
    
    return (
        <div className="text-center position-relative w-100 h-100">
            <i className="bi bi-x-circle position-absolute top-0 end-0 fa-lg" onClick={exitViewServer}></i>

            {server ?

            <div className="container start-50 translate-middle-x top-0 position-absolute w-100 mt-4 m-1 border border-secondary p-3">
                <div className="row ">
                    <div className="col-md-4 position-relative"  >
                        <div className="position-relative" onMouseOver={mouseOverEvent} onMouseLeave={mouseLeaveEvent} onClick={onClickAvatar}>
                            {hoverOverAvatar ? <img alt="Click to change the avatar." style={{borderRadius:'50%', objectFit:'cover', height:'100px', width:'100px'}} className="thumbnail-image start-50 top-50 translate-middle position-absolute" src={`${process.env.PUBLIC_URL}/images/server_default.png`}/> : ""}
                            <Avatar large={true} localPreview={previewAvatar} avatarPostId={server.avatarPostId}/>
                            <input type="file" onChange={changeAvatar} name="file-upload" id="file" ref={formRef} style={{ display: "none" }}/>
                        </div>
                    </div>
                    <div className="col-md-8 mt-5">
                        <div className="row">
                            {(permission >= 5) ? 
                                <input type="text" value={editServerName} onInput={serverNameEvent} className="form-control w-75" rows="1" placeholder="Server Name" aria-label="Server Name"/>
                            : server.serverName}
                            
                        </div>
                        <div className="row">
                            Server ID: {server._id}
                        </div>
                    </div>
                </div>
                <div className="row m-4">
                    <Tabs defaultActiveKey="users" id="uncontrolled-tab-example" className="mb-3">
                        <Tab eventKey="users" title="Users">
                            <div className="container">
                                {serverUsers ?
                                    <div className="row">
                                        {
                                            serverUsers.map((el, i) => (
                                                <div className="col-md-4" key={el._id}>
                                                    <UserThumbnail userId={el.userId}/>
                                                </div>
                                            ))
                                        }
                                    </div>
                                : (loadedUsers ? "You do not have permission to view this server's users." : <Loading/>)}
                            </div>
                        </Tab>
                        <Tab eventKey="settings" title="Server Settings">
                            {loadedSecurities ?
                                <Form>
                                    <div className="row">
                                        <Form.Label className="my-auto col-md-6">Who can view posts?</Form.Label>
                                        <div key={`default-radio`} className="ps-5 my-auto col-md-6 border-start border-secondary">
                                            <Form.Check type='radio' id={`security-radio-0-0`} label={`Anyone`} checked={serverSecurities.postsVisibility === 0} onChange={securitySettingEvent}/>
                                            <Form.Check type='radio' id={`security-radio-0-1`} label={`Members Only`} checked={serverSecurities.postsVisibility === 1} onChange={securitySettingEvent}/>
                                        </div>
                                        <Form.Label className="my-auto col-md-6">Who can join?</Form.Label>
                                        <div key={`default-radio`} className="ps-5 my-auto col-md-6 border-start border-secondary">
                                            <Form.Check type='radio' id={`security-radio-1-0`} label={`Anyone with a link`} checked={serverSecurities.allowJoin === 0} onChange={securitySettingEvent}/>
                                            <Form.Check type='radio' id={`security-radio-1-1`} label={`Invite only`} checked={serverSecurities.allowJoin === 1} onChange={securitySettingEvent}/>
                                            <Form.Check type='radio' id={`security-radio-1-2`} label={`Nobody`} checked={serverSecurities.allowJoin === 2} onChange={securitySettingEvent}/>
                                        </div>
                                        <Form.Label className="my-auto col-md-6">Who can comment on posts?</Form.Label>
                                        <div key={`default-radio`} className="ps-5 my-auto col-md-6 border-start border-secondary">
                                            <Form.Check type='radio' id={`security-radio-2-0`} label={`Anyone`} checked={serverSecurities.allowComment === 0} onChange={securitySettingEvent}/>
                                            <Form.Check type='radio' id={`security-radio-2-1`} label={`Members`} checked={serverSecurities.allowComment === 1} onChange={securitySettingEvent}/>
                                        </div>
                                        <Form.Label className="my-auto col-md-6">Who can view members of the server?</Form.Label>
                                        <div key={`default-radio`} className="ps-5 my-auto col-md-6 border-start border-secondary">
                                            <Form.Check type='radio' id={`security-radio-3-0`} label={`Anyone`} checked={serverSecurities.usersVisibility === 0} onChange={securitySettingEvent}/>
                                            <Form.Check type='radio' id={`security-radio-3-1`} label={`Members`} checked={serverSecurities.usersVisibility === 1} onChange={securitySettingEvent}/>
                                        </div>
                                    </div>
                                </Form>
                            : <Loading/>}
                        </Tab>
                    </Tabs>
                </div>

                <div className="row justify-content-end">
                    {(permission >= 5) ? <div  className="justify-content-center mt-5 mx-auto text-center w-25 row">
                            {madeChanges ? <button type="button" onClick={saveChanges} className="btn-md btn-outline-primary">Save Changes</button> : "Changes Saved"}
                        </div>
                    : ""}
                </div>
                
                
                
            </div>

            : <Loading/> }
            
        </div>
    )
}

export default ViewServer;
