import React, {useEffect, useState} from 'react';
import '../css/Thumbnail.css';
import axios from 'axios'

function Avatar(props) {

    const [avatarDataId, setAvatarDataId] = useState();

    useEffect(() => {
        if (props.avatarPostId != null) {
            axios.get(`http://localhost:5000/files/getImageFromPost/${props.avatarPostId}`, {headers: 
                {
                "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200) {
                    setAvatarDataId(res.data.dataId);
                }
            }).catch((err) => {
                console.log(err);
            });
        }
    }, [props.avatarPostId]);

    return(
        <img style={{borderRadius:'50%', objectFit:'cover', height: props.large ? '150px' : '50px', width: props.large ? '150px' : '50px'}} 
            className="thumbnail-image" 
            src={props.localPreview ? props.localPreview :
                (avatarDataId ? `http://localhost:5000/files/image/${avatarDataId}` : `${process.env.PUBLIC_URL}/images/server_default.png`)}/>
    );
}

export default Avatar;
