import React, {useState, useEffect} from 'react';
import axios from 'axios';

import LeftSidebar from './LeftSidebar';
import Main from './Main';
import RightSidebar from './RightSidebar';
import Loading from './Loading';
import Login from './Login';
import ServerCreation from './ServerCreation';
import PostCreation from './PostCreation';
import ViewPost from './ViewPost'


function App(props) {
    const [loading, setLoading] = useState(false);
    const [loggedIn, setLoggedIn] = useState(false);
    const [currentServerId, setCurrentServerId] = useState(null);
    const [viewPostId, setViewPostId] = useState();

    // {
    //     username
    //     _id
    //     avatarPostId
    // });
    const [userInfo, setUserInfo] = useState();


    //0 - main
    //1 - create server
    //2 - new post
    //3 - view post
    const [componentDisplay, setComponentDisplay] = useState(0);


    useEffect(() => {
        if (!localStorage.getItem("authToken")){
            setLoading(false);
            setLoggedIn(false);
        } else {
            verifyAuth();
        }
    })


    const verifyAuth = () => {
        axios.get('http://localhost:5000/account/verifyAuth', {
            headers: {
                "x-access-token": localStorage.getItem("authToken")
                // "Access-Control-Allow-Origin": "*"
            }
            }).then((res) => {
                if (res.status === 200) {
                    setCurrentServerId(localStorage.getItem("mostRecentServer"));
                    setUserInfo(res.data)
                    setLoggedIn(true);

                }
            }).catch((err) => {
                localStorage.clear();
            });
    }

    //Log out user
    const logout = () => {
        localStorage.clear();
        setLoading(true);
        setLoggedIn(false);
    }

    //Changes the server the user is viewing and updates cookies.
    const changeServer = (serverId) => {
        localStorage.setItem("mostRecentServer", serverId);
        setCurrentServerId(serverId);
    }

    //Switch to post creation mode
    const enterPostCreation = () => {
        setComponentDisplay(2);
    }

    //Switch to view post mode with postId
    const viewPost = (postId) => {
        setViewPostId(postId);
        setComponentDisplay(3);
    }


    if (loading) {
        return(
            <div className="wrapper w-100 h-100 mw-100 mh-100">
                <Loading/>
            </div>
        )
    } else if (loggedIn) {
        var component = <div>An error has occured</div>;

        if (componentDisplay === 0 && (currentServerId != null)) {
            component = <Main currentServerId={currentServerId} viewPost={viewPost} enterPostCreation={enterPostCreation}/>
        } else if (componentDisplay === 1) {
            component = <ServerCreation setComponentDisplay={setComponentDisplay} changeServer={changeServer}/>
        } else if (componentDisplay === 2) {
            component = <PostCreation setComponentDisplay={setComponentDisplay} currentServerId={currentServerId}/>
        } else if (componentDisplay === 3) {
            component = <ViewPost setComponentDisplay={setComponentDisplay} postId={viewPostId}/>
        }

        return(
            <div className="w-100 h-100 container-fluid">
                <div className="row">
                    <div  className="col border border-secondary">
                        <LeftSidebar setComponentDisplay={setComponentDisplay} changeServerCallback={changeServer}/>
                    </div>
                    <div className="col-7 border border-secondary">
                        {component}
                    </div>
                    <div  className="col border border-secondary">
                        <RightSidebar currentServerId={currentServerId} userInfo={userInfo}/>
                    </div>
                </div>
                {/* <button type="button" class="btn btn-outline-primary" onClick={logout}>Log Out</button> */}
            </div>
        )

    } else {
        return(
            <div className="wrapper w-100 h-100">
                <Login verifyAuth={verifyAuth}/>
            </div>
        )
    }
    
} 

export default App;
