import React, {useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Avatar from '../thumbnails/Avatar';
import {Dropdown, DropdownButton} from 'react-bootstrap';

import '../css/Thumbnail.css';

function UserThumbnail(props) {

    const [profileObject, setProfileObject] = useState({});
    const [userId, setUserId] = useState();
    const navigate = useNavigate();

    useEffect(() => {
        if (props.userId != null) {
            //These are permission objects it's receiving
            axios.get(`http://localhost:5000/accounts/getUser/${props.userId}`, {headers: 
                {
                "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                if (res.status === 200){
                    setProfileObject(res.data.profileObject);
                    setUserId(res.data.userId);
                }
            });
        } else {
            //maybe populate it with friends
        }
    }, []);

    //Handles the drop down menu for the post.
    const handleSelect = (e) => {
        if (e === "view"){
            navigate(`/profile/${profileObject._id}`);
        }
    }

    return(
        <div className="container thumbnail-wrapper w-90">
           
            <div className="row h-100">
                <div className="col-md-4 my-auto">
                    <Avatar avatarPostId={profileObject.avatarPostId}/>
                </div>
                <div className="col-md-6 my-auto">
                    {profileObject.username}
                </div>
                <div className="col-md-2">
                    <DropdownButton className="m-2" title="" id="dropdown-basic-button" variant="secondary" size="sm" onSelect={handleSelect}>
                        <Dropdown.Item eventKey="view">View Profile</Dropdown.Item>
                    </DropdownButton>
                </div>
            </div>
        </div>

    );
}

export default UserThumbnail;
