import React, {useState} from 'react';
import '../css/Thumbnail.css';
import {Dropdown, DropdownButton} from 'react-bootstrap';
import axios from 'axios';

function User(props) {

    const clickedThumbnail = (e) => {
        //do nothing yet
    }

    const handleSelect = (e) => {
        if (e === "delete"){
            console.log(`http://localhost:5000/posts/deleteComment/${props.serverId}/${props.commentId}`)
            axios.post(`http://localhost:5000/posts/deleteComment/${props.serverId}/${props.commentId}`, {headers: 
            {
                "x-access-token": localStorage.getItem("authToken"),
            }})
            .then((res) => {
                if (res.status === 401){
                }
            });
        } else if (e === "report"){
            
        }
    }

    return(
        <div className="container thumbnail-wrapper w-90 m-2 border border-secondary" onClick={clickedThumbnail}>
            <div className="position-absolute end-0 me-2">
                <DropdownButton title="" id="dropdown-basic-button" variant="secondary" size="sm" onSelect={handleSelect}>
                    {(props.authorId === props.userInfo._id )? <Dropdown.Item eventKey="delete">Delete</Dropdown.Item> : ""}
                    <Dropdown.Item eventKey="report">Report</Dropdown.Item>
                </DropdownButton>
            </div>
            <div className="row h-100">
                <div className="col-md-2 my-3">
                    <img className="thumbnail-image" src={props.imageDataId ? `http://localhost:5000/files/image/${props.imageDataId}` : `${process.env.PUBLIC_URL}/images/user_default.png`}/>
                </div>
                <div className="col-md-10 my-auto">
                    <div className="border-bottom border-secondary text-center">
                        <h5>{props.authorName}</h5>
                    </div>
                    <div className="w-100 p-2">
                        {props.commentText}
                    </div>
                </div>
            </div>
        </div>

    );
}

export default User;
