import React from 'react';
import UploadBar from './UploadBar'
import ImageThumbnail from './ImageThumbnail'
import './css/Main.css'
import axios from 'axios'


const TO_DISPLAY = 8;

class Main extends React.Component {

    constructor(props) {
        super(props);
        this.state = {images: []};
      }

    render() {
        console.log("main render");
        axios.get('http://localhost:5000/file/getImages')
        .then((res) => {
            res.data.map((el, i) => {
                this.setState(prevState => ({
                    images: [...prevState.images, el.id]
                  }))
                
            });
            console.log(this.state);
        });
        return(
                <div className="row gy-4 main-grid mx-auto pt-3">
                    {
                        this.state.images.map((el, i) => {
                            console.log("pppp");
                            <ImageThumbnail/>
                        })
                    }
                </div> 
        )
    }
} 

export default Main;
