import React, {useState} from 'react';
import axios from 'axios';
import '../css/Thumbnail.css';
import Avatar from '../thumbnails/Avatar';

function ServerThumbnail(props) {
    //Server thumbnail currently receives all the information it needs from LeftSidebar
    //Avatar excluded.

    const clickedThumbnail = (e) => {
        props.changeServerCallback(props.serverObject._id);
    }

    return(
        <div className="thumbnail-wrapper w-90 m-2 border border-secondary row" onClick={clickedThumbnail}>
            <div className="col my-auto">
                <Avatar avatarPostId={props.serverObject.avatarPostId}/>
            </div>
            <div className="thumbnail-name col my-auto">
                {props.serverObject.serverName}
            </div>
        </div>

    );
}

export default ServerThumbnail;
