import React, {useState, useEffect} from 'react';
import UploadBar from './UploadBar'
import ImageThumbnail from './ImageThumbnail'
import './css/Main.css'
import axios from 'axios'

function Main(props) {

    const [serverDisplay, setServerDisplay] = useState();
    const [imageThumbnails, setImageThumbnails] = useState([]);
    const [uploadBar, setUploadBar] = useState();

    useEffect(() => {
        if (props.currentServerId != null) {
            setServerDisplay(imageThumbnails);
            fetchData();
        } 
    }, []);

     //Callback to display new files when they are uploaded by the user.
     const addedNewFile = (id) => {
        setImageThumbnails([...imageThumbnails, <ImageThumbnail id={id}/>]);
    }

    //Retrieve files from the server
    const fetchData = () => {
        console.log("fetch");
        axios.get(`http://localhost:5000/servers/getImages/${props.currentServerId}`, {headers: 
            {
                "x-access-token": localStorage.getItem("authToken")
            }
        })
        .then((res) => {
            let newThumbnails = [];
            res.data.map((el, i) => {
                newThumbnails.push(<ImageThumbnail id={el.dataId} key={el.dataId}/>);
            });
           
            setImageThumbnails([...imageThumbnails, ...newThumbnails]);
            
            setUploadBar(<UploadBar newFileCallback={addedNewFile} currentServerId={props.currentServerId}/>);
        });
    }

   

    return(
        <div>
            <div className="row gy-4 main-grid mx-auto pt-3">
                {imageThumbnails}
            </div> 
            {uploadBar}
        </div>
    )
} 

export default Main;
