import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {Route, Routes, BrowserRouter } from 'react-router-dom';

import LeftSidebar from './LeftSidebar';
import Main from './Main';
import RightSidebar from './RightSidebar';
import Loading from './Loading';
import Login from './Login';
import ServerCreation from './ServerCreation';
import PostCreation from './PostCreation';
import ViewPost from './ViewPost'
import ViewProfile from './ViewProfile'


function App(props) {
    const [loading, setLoading] = useState(false);
    const [loggedIn, setLoggedIn] = useState(false);
    const [currentServerId, setCurrentServerId] = useState(null);
    const [viewPostObject, setViewPostObject] = useState();
    const [viewProfileObject, setViewProfileObject] = useState();

    // {
    //     username
    //     _id
    //     avatarPostId
    // });
    const [userInfo, setUserInfo] = useState();


    //0 - main
    //1 - create server
    //2 - new post
    //3 - view post
    const [componentDisplay, setComponentDisplay] = useState(0);


    useEffect(() => {
        if (!localStorage.getItem("authToken")){
            setLoading(false);
            setLoggedIn(false);
        } else {
            verifyAuth();
        }
    }, [])


    const verifyAuth = () => {
        axios.get('http://localhost:5000/accounts/verifyAuth', {
            headers: {
                "x-access-token": localStorage.getItem("authToken")
                // "Access-Control-Allow-Origin": "*"
            }
            }).then((res) => {
                if (res.status === 200) {

                    setCurrentServerId(localStorage.getItem("mostRecentServer"));
                    setUserInfo(res.data)
                    setLoggedIn(true);

                }
            }).catch((err) => {
                localStorage.clear();
            });
    }

    //Log out user
    const logout = () => {
        localStorage.clear();
        setLoading(true);
        setLoggedIn(false);
    }

    
    //This one exists to force a rerender of LeftSidebar once a new server has been created.
    //Doesn't seem like the right way to do it but I can't think of anything better.
    const [newServer, setNewServer] = useState(false);

    const refreshLeftSidebar = () => {
        setNewServer(!newServer);
    }

    //Changes the server the user is viewing and updates cookies.
    const changeServer = (serverId) => {
        localStorage.setItem("mostRecentServer", serverId);
        setComponentDisplay(0);
        setCurrentServerId(serverId);
    }

    //Switch to post creation mode
    const enterPostCreation = () => {
        setComponentDisplay(2);
    }

    //Switch to view post mode with postId
    const viewPost = (postObject) => {
        setViewPostObject(postObject);
        setComponentDisplay(3);
    }

    const viewProfile = (profileObject) => {
        setViewProfileObject(profileObject);
        setComponentDisplay(4);
    }


    if (loading) {
        return(
            <div className="wrapper w-100 h-100 mw-100 mh-100">
                <Loading/>
            </div>
        )
    } else if (loggedIn) {
        var component = <div>An error has occured</div>;

        // if (componentDisplay === 0 && (currentServerId != null)) {
        //     component = <Main currentServerId={currentServerId} viewPost={viewPost} enterPostCreation={enterPostCreation}/>
        // } else if (componentDisplay === 1) {
        //     component = <ServerCreation refreshLeftSidebar={refreshLeftSidebar} setComponentDisplay={setComponentDisplay} changeServer={changeServer}/>
        // } else if (componentDisplay === 2 && (currentServerId != null)) {
        //     component = <PostCreation setComponentDisplay={setComponentDisplay} currentServerId={currentServerId}/>
        // } else if (componentDisplay === 3) {
        //     component = <ViewPost setComponentDisplay={setComponentDisplay} postObject={viewPostObject} userInfo={userInfo} viewProfileCallback={viewProfile}/>
        // } else if (componentDisplay === 4) {
        //     component = <ViewProfile setComponentDisplay={setComponentDisplay} profileObject={viewProfileObject} userInfo={userInfo}/>
        // }

        return(
            <div className="w-100 vh-100 container-fluid">
                <div className="row">
                    <div  className="col vh-100 border border-secondary full-height">
                        <LeftSidebar newServer={newServer} setComponentDisplay={setComponentDisplay} changeServerCallback={changeServer}/>
                    </div>
                    <div className="overflow-auto col-8 vh-100 border border-secondary full-height">
                    <BrowserRouter>
                        <Routes>
                            <Route path="/s/:currentServerId" element={<Main viewPost={viewPost} enterPostCreation={enterPostCreation}/>} />
                            <Route path="/createServer" element={ <ServerCreation refreshLeftSidebar={refreshLeftSidebar} setComponentDisplay={setComponentDisplay} changeServer={changeServer}/>} />
                            <Route path="/post/:currentServerId" element={<PostCreation setComponentDisplay={setComponentDisplay}/>}/>
                            <Route path="/v/:currentServerId/:postId" element={<ViewPost setComponentDisplay={setComponentDisplay} userInfo={userInfo} viewProfileCallback={viewProfile}/>}/>
                            <Route path="/profile/:userId" element={<ViewProfile setComponentDisplay={setComponentDisplay} profileObject={viewProfileObject} userInfo={userInfo}/>}/>
                        </Routes>
                    </BrowserRouter>
                    </div>
                    <div  className="col vh-100 border border-secondary">
                        <RightSidebar currentServerId={currentServerId} userInfo={userInfo} viewProfileCallback={viewProfile} logoutCallback={logout}/>
                    </div>
                </div>
                {/* <button type="button" class="btn btn-outline-primary" onClick={logout}>Log Out</button> */}
            </div>
        )

    } else {
        return(
            <div className="wrapper w-100 h-100">
                <Login verifyAuth={verifyAuth}/>
            </div>
        )
    }
    
} 

export default App;
