import React from 'react';
import axios from 'axios';

import LeftSidebar from './LeftSidebar';
import Main from './Main';
import RightSidebar from './RightSidebar';
import UploadBar from './UploadBar';
import Loading from './Loading';
import Login from './Login';
import './css/App.css'
import verifyAuth from '../../../server/middleware/verifyAuth';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {loading: true, loggedIn: false};  
    }

    componentDidMount() {
        if (!localStorage.getItem("authToken")){
            console.log("not logged in");
            this.setState({loading: false, loggedIn: false});
        } else {
            verifyAuth();
        }
    }

    verifyAuth = () => {
        axios.post('http://localhost:5000/account/verifyAuth', {
                headers: {
                    "x-access-token": localStorage.getItem("authToken")
                }
            }).then((res) => {
                console.log("returned");
                console.log(res);
            });
    }

    render() {
        if (this.state.loading) {
            return(
                <div className="wrapper w-100 h-100 mw-100 mh-100">
                    <Loading/>
                </div>
            )
        } else if (this.state.loggedIn) {
            return(
                <div className="wrapper w-100 h-100">
                    <LeftSidebar className="leftsidebar"/>
                    <div className="center">
                        <Main className="main"/>
                    </div>
                    <RightSidebar className="rightsidebar"/>
                </div>
            )
        } else {
            return(
                <div className="wrapper w-100 h-100">
                    <Login />
                </div>
            )
        }
    }
} 

export default App;
